    def compute_single(self,data):
        #data = np.array(data,dtype=np.float128)
        id = data[0]
        h = data[1]
        #h = np.array(h,dtype=np.float128)
        v = self.v
        p = self.probn[id]
        f1 = lambda x: self.f([x[0],v[1]],[h,x[1]],p)
        f2 = lambda x: self.f([v[0],x[0]],[x[1],h],p)
        #v1,_ = fsolve(f1,[0,0])#there maybe multiple solutions, depends on the initial point
        #v2,_ = fsolve(f2,[0,0])
        #nlcon1 = NonlinearConstraint(f1,lb=0,ub=np.array([1,1/p[0]-1/p[1]]))
        nlcon1 = NonlinearConstraint(f1,lb=0,ub=np.array([1,np.infty]))
        con1 = LinearConstraint(np.diag([1,1]),lb=[0,h+1.001],ub=[v[1],h+1e5],keep_feasible=False)
        #nlcon2 = NonlinearConstraint(f2,lb=0,ub=np.array([1,1/p[0]-1/p[1]]))
        nlcon2 = NonlinearConstraint(f2,lb=0,ub=np.array([np.infty,1/p[0]-1/p[1]]))
        con2 = LinearConstraint(np.diag([1,1]),lb=[v[0],0],ub=[1e6,h-1.001],keep_feasible=False)
        obj_func = lambda x: -x[0]
        v1,_ = minimize(obj_func, [0,h+1.001],constraints=[nlcon1,con1]).x
        v1 = np.minimum(v1,20000)
        try:
            v2,_ = minimize(obj_func, [v[0],0],constraints=[nlcon2,con2]).x
        except:
            v2 = v[1]
        v2 = np.minimum(v2,20000)
        # we can also use another method, to minimize the mse loss
        #con1 = LinearConstraint(np.array([[-1,0],[1,0],[0,-1]]),ub=np.array([0,v[1],-h]))
        #con2 = LinearConstraint(np.array([[-1,0],[0,-1],[0,1]]),ub=np.array([-v[0],0,h]))
        #v1,_ = minimize(f1,[0,0],constraints=con1).x
        #v2,_ = minimize(f2,[0,0],constraints=con2).x
        return [id,v1,v2]
    
    def compute(self,h,v,mt=False):
        #generate h with index
        self.v = v
        input = np.concatenate((np.arange(0,len(h)),h),0).reshape(2,-1).transpose(1,0)
        
        ''' test for single process
        start = time.time()
        for i in range(len(h)):
            x = self.compute_single(input[i])
        end = time.time()
        print('single process time:{:.5f}s'.format(end-start))
        '''
        #start = time.time()
        if mt:
            with Pool(20) as p:
                x = p.map(self.compute_single,input)
                p.close()
                p.join()
        else:
            x = []
            for i in range(len(h)):
                x.append(self.compute_single(input[i]))
        #end = time.time()
        #print('multi-process timetime:{:.5f}s'.format(end-start))
    
        return np.array(x)
    
